INSERT INTO EDITION VALUES('This book constitutes the refereed proceedings of the 9th International Conference on Applied Cryptography and Network Security, ACNS 2011, held in Nerja, Spain, in June 2011. The 31 revised full papers included in this volume were carefully reviewed and selected from 172 submissions. They are organized in topical sessions on malware and intrusion detection; attacks, applied crypto; signatures and friends; eclectic assortment; theory; encryption; broadcast encryption; and security services.', '22', 'http://books.google.com/books/content?id=OrBI6J5wHewC&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=OrBI6J5wHewC&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 552, 2.4, 586, true, PARSEDATETIME('2011', 'yyyy'), 9, 13, 3);
