INSERT INTO BOOK_TYPE VALUES(2, 55, 'Cryptography: A Very Short Introduction');
INSERT INTO EDITION VALUES('This Very Short Introduction provides a clear and informative introduction to the science of codebreaking, and its explosive impact on modern society. Taking the reader through the actual processes of developing codes and deciphering them, the book explains what algorithms do, how they are used, the risks associated with using them, and why governments should be concerned. Written in a fluid and lively style to appeal to the non-mathematical reader, this makes for fascinating reading.', '22', 'http://books.google.com/books/content?id=HvR0DgAAQBAJ&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=HvR0DgAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 142, 4.6, 406, false, PARSEDATETIME('2002', 'yyyy'), 2, 2, 1);
