INSERT INTO BOOK_TYPE VALUES(53, 59, 'Django Design Patterns and Best Practices');
INSERT INTO EDITION VALUES('If you want to learn how best to utilize commonly found patterns and learn best practices in developing applications with Django, this is the book for you. This book, like Django itself, is accessible to amateur and professional developers alike and assumes little in the way of prior experience. Although written for Python 3, the majority of the code in this book works in Python 2 or can be easily translated.', '19', 'http://books.google.com/books/content?id=WzW9BwAAQBAJ&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=WzW9BwAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 222, 3.9, 449, true, PARSEDATETIME('2015', 'yyyy'), 53, 87, 25);
