INSERT INTO EDITION VALUES(2, 41, 'This Very Short Introduction provides a clear and informative introduction to the science of codebreaking, and its explosive impact on modern society. Taking the reader through the actual processes of developing codes and deciphering them, the book explains what algorithms do, how they are used, the risks associated with using them, and why governments should be concerned. Written in a fluid and lively style to appeal to the non-mathematical reader, this makes for fascinating reading.', '19', 'http://books.google.com/books/content?id=HvR0DgAAQBAJ&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=HvR0DgAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 142, 4.0, 295, true, PARSEDATETIME('2002', 'yyyy'), 1, 2);INSERT INTO BOOK_TITLE_EDITIONS VALUES(2, 2);
