INSERT INTO BOOK_TITLE VALUES(5, 'Understanding Cryptography');
INSERT INTO EDITION VALUES(5, 33, 'Cryptography is now ubiquitous – moving beyond the traditional environments, such as government communications and banking systems, we see cryptographic techniques realized in Web browsers, e-mail programs, cell phones, manufacturing systems, embedded software, smart buildings, cars, and even medical implants. Today''s designers need a comprehensive understanding of applied cryptography. After an introduction to cryptography and data security, the authors explain the main techniques in modern cryptography, with chapters addressing stream ciphers, the Data Encryption Standard (DES) and 3DES, the Advanced Encryption Standard (AES), block ciphers, the RSA cryptosystem, public-key cryptosystems based on the discrete logarithm problem, elliptic-curve cryptography (ECC), digital signatures, hash functions, Message Authentication Codes (MACs), and methods for key establishment, including certificates and public-key infrastructure (PKI).', '22', 'http://books.google.com/books/content?id=f24wFELSzkoC&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=f24wFELSzkoC&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 372, 4.1, 150, false, PARSEDATETIME('2009', 'yyyy'), 2, 5);INSERT INTO BOOK_TITLE_EDITIONS VALUES(5, 5);
