INSERT INTO EDITION VALUES(1, 17, 'Cryptography is the most effective way to achieve data securityand is essential to e-commerce activities such as online shopping,stock trading, and banking This invaluable introduction to the basics of encryption coverseverything from the terminology used in the field to specifictechnologies to the pros and cons of different implementations Discusses specific technologies that incorporate cryptographyin their design, such as authentication methods, wirelessencryption, e-commerce, and smart cards Based entirely on real-world issues and situations, thematerial provides instructions for already available technologiesthat readers can put to work immediately Expert author Chey Cobb is retired from the NRO, where she helda Top Secret security clearance, instructed employees of the CIAand NSA on computer security and helped develop the computersecurity policies used by all U.S. intelligence age', '22', 'http://books.google.com/books/content?id=b3pQDh7j5ogC&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=b3pQDh7j5ogC&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 324, 2.5, 186, true, PARSEDATETIME('2004', 'yyyy'), 0, 1);INSERT INTO BOOK_TITLE_EDITIONS VALUES(1, 1);
