INSERT INTO BOOK_TITLE VALUES(6, 'Theory of Cryptography');
INSERT INTO EDITION VALUES(6, 24, 'This book constitutes the refereed proceedings of the Second Theory of Cryptography Conference, TCC 2005, held in Cambridge, MA, USA in February 2005. The 32 revised full papers presented were carefully reviewed and selected from 84 submissions. The papers are organized in topical sections on hardness amplification and error correction, graphs and groups, simulation and secure computation, security of encryption, steganography and zero knowledge, secure computation, quantum cryptography and universal composability, cryptographic primitives and security, encryption and signatures, and information theoretic cryptography.', '19', 'http://books.google.com/books/content?id=6g5nI71vgiIC&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=6g5nI71vgiIC&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 619, 1.8, 158, false, PARSEDATETIME('2005', 'yyyy'), 2, 6);INSERT INTO BOOK_TITLE_EDITIONS VALUES(6, 6);
