INSERT INTO EDITION VALUES(8, 25, 'This book explains the basic methods of modern cryptography. It is written for readers with only basic mathematical knowledge who are interested in modern cryptographic algorithms and their mathematical foundation. Several exercises are included following each chapter. From the reviews: "Gives a clear and systematic introduction into the subject whose popularity is ever increasing, and can be recommended to all who would like to learn about cryptography." --ZENTRALBLATT MATH', '19', 'http://books.google.com/books/content?id=BuQlBQAAQBAJ&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=BuQlBQAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 338, 2.3, 78, true, PARSEDATETIME('2013', 'yyyy'), 2, 8);INSERT INTO BOOK_TITLE_EDITIONS VALUES(8, 8);
