INSERT INTO EDITION VALUES(12, 32, 'Stallings provides a survey of the principles and practice of cryptography and network security. This edition has been updated to reflect the latest developments in the field. It has also been extensively reorganized to provide the optimal sequence for classroom instruction and self-study.', '21', 'http://books.google.com/books/content?id=wwfTvrWEKVwC&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=wwfTvrWEKVwC&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 719, 3.6, 184, false, PARSEDATETIME('2011', 'yyyy'), 5, 12);INSERT INTO BOOK_TITLE_EDITIONS VALUES(12, 12);
