INSERT INTO BOOK_TITLE VALUES(13, 'A Course in Number Theory and Cryptography');
INSERT INTO EDITION VALUES(13, 23, 'The purpose of this book is to introduce the reader to arithmetic topics, both ancient and modern, that have been at the center of interest in applications of number theory, particularly in cryptography. Because number theory and cryptography are fast-moving fields, this new edition contains substantial revisions and updated references.', '20', 'http://books.google.com/books/content?id=4eMlBQAAQBAJ&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=4eMlBQAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 208, 1.6, 87, false, PARSEDATETIME('2012', 'yyyy'), 2, 13);INSERT INTO BOOK_TITLE_EDITIONS VALUES(13, 13);
