INSERT INTO BOOK_TITLE VALUES(15, 'Cryptography and Security: From Theory to Applications');
INSERT INTO EDITION VALUES(15, 24, 'This Festschrift volume, published in honor of Jean-Jaques Quisquater on the occasion of his 65th Birthday, contains 33 papers from colleagues all over the world and deals with all the fields to which Jean-Jacques dedicated his work during his academic career. Focusing on personal tributes and re-visits of Jean-Jacques Quisquater''s legacy, the volume addresses the following central topics: symmetric and asymmetric cryptography, side-channels attacks, hardware and implementations, smart cards, and information security. In addition there are four more contributions just "as diverse as Jean-Jacques'' scientific interests".', '21', 'http://books.google.com/books/content?id=R0ZqLmhFvkMC&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=R0ZqLmhFvkMC&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 502, 1.1, 487, true, PARSEDATETIME('2012', 'yyyy'), 2, 15);INSERT INTO BOOK_TITLE_EDITIONS VALUES(15, 15);
