INSERT INTO BOOK_TITLE VALUES(22, 'Modern Cryptography and Elliptic Curves: A Beginner’s Guide');
INSERT INTO EDITION VALUES(22, 23, 'This book offers the beginning undergraduate student some of the vista of modern mathematics by developing and presenting the tools needed to gain an understanding of the arithmetic of elliptic curves over finite fields and their applications to modern cryptography. This gradual introduction also makes a significant effort to teach students how to produce or discover a proof by presenting mathematics as an exploration, and at the same time, it provides the necessary mathematical underpinnings to investigate the practical and implementation side of elliptic curve cryptography (ECC). Elements of abstract algebra, number theory, and affine and projective geometry are introduced and developed, and their interplay is exploited. Algebra and geometry combine to characterize congruent numbers via rational points on the unit circle, and group law for the set of points on an elliptic curve arises from geometric intuition provided by Bézout''s theorem as well as the construction of projective spa', '20', 'http://books.google.com/books/content?id=TQIvDwAAQBAJ&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=TQIvDwAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 252, 1.1, 54, true, PARSEDATETIME('2017', 'yyyy'), 9, 22);INSERT INTO BOOK_TITLE_EDITIONS VALUES(22, 22);
