INSERT INTO BOOK_TITLE VALUES(36, 'Beyond the Basic Stuff with Python');
INSERT INTO EDITION VALUES(36, 36, 'You''re a student who wants to jumpstart their career with practical skills, or you''re a self-taught beginner who has learned all you can from beginner programmer books and coding bootcamps. Now you''re looking for the next step to becoming a real-world professional programmer so you can create your own apps and get started with your career. If that fits, then this book is for you! This book is perfect for self-taught programmers looking for the stuff intro books don''t teach you and students wanting to get practical information before getting started with applying their new programming skills.', '22', 'http://books.google.com/books/content?id=7GUKEAAAQBAJ&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=7GUKEAAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'un', 448, 1.7, 57, true, PARSEDATETIME('2020', 'yyyy'), 10, 36);INSERT INTO BOOK_TITLE_EDITIONS VALUES(36, 36);
