INSERT INTO BOOK_TITLE VALUES(183, 'A Pascal Database Book');
INSERT INTO EDITION VALUES(183, 39, 'This pioneering work integrates an introduction to databases into a comprehensive survey of basic programming, and thus allows beginning computer scientists to relate database technology to their core studies as early and as thoroughly as possible. Ullmann provides a computer-free introduction to data processing; imparts skill in relational algebra, normalized file design, and design of access-path data structures, by means of copious examples and exercises; and gives full accounts of file organization and database administration. Students of computer science at many levels will find this book a useful bridge between standard PASCAL programming and the latest developments in database technology.', '22', 'http://books.google.com/books/content?id=reomAAAAMAAJ&printsec=frontcover&img=1&source=gbs_api', 'http://books.google.com/books/content?id=reomAAAAMAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'en', 332, 3.2, 103, true, PARSEDATETIME('1985', 'yyyy'), 54, 183);INSERT INTO BOOK_TITLE_EDITIONS VALUES(183, 183);
